/**
 * @description 保存原始 window.open 方法的引用
 */
const originalWindowOpen = window.open

/**
 * @description 处理链接点击事件，拦截新标签页打开
 * @param {Event} e 点击事件对象
 */
const hookClick = (e) => {
    const origin = e.target.closest('a')
    const isBaseTargetBlank = document.querySelector(
        'head base[target="_blank"]'
    )
    console.log('origin', origin, isBaseTargetBlank)
    if (
        (origin && origin.href && origin.target === '_blank') ||
        (origin && origin.href && isBaseTargetBlank)
    ) {
        e.preventDefault()
        console.log('handle origin', origin)
        // 使用多重跳转策略确保在 WebView 中生效
        handleNavigation(origin.href)
    } else {
        console.log('not handle origin', origin)
    }
}

/**
 * @description 统一的导航处理函数，多重降级策略确保跳转成功
 * @param {string} url 目标 URL
 */
function handleNavigation(url) {
    if (!url) {
        console.warn('[handleNavigation] URL 为空')
        return
    }

    console.log('[handleNavigation] 准备跳转:', url)

    // 策略1: 尝试使用 window.location.assign (最标准的方式)
    try {
        window.location.assign(url)
        console.log('[handleNavigation] 使用 location.assign 跳转')
        return
    } catch (e) {
        console.warn('[handleNavigation] location.assign 失败:', e)
    }

    // 策略2: 直接设置 location.href (降级方案)
    try {
        window.location.href = url
        console.log('[handleNavigation] 使用 location.href 跳转')
        return
    } catch (e) {
        console.warn('[handleNavigation] location.href 失败:', e)
    }

    // 策略3: 使用 location.replace (强制跳转，不保留历史)
    try {
        window.location.replace(url)
        console.log('[handleNavigation] 使用 location.replace 跳转')
        return
    } catch (e) {
        console.warn('[handleNavigation] location.replace 失败:', e)
    }

    // 策略4: 创建隐藏的 <a> 标签并触发点击 (模拟用户点击)
    try {
        const a = document.createElement('a')
        a.href = url
        a.style.display = 'none'
        document.body.appendChild(a)
        a.click()
        document.body.removeChild(a)
        console.log('[handleNavigation] 使用模拟点击跳转')
    } catch (e) {
        console.error('[handleNavigation] 所有跳转策略均失败:', e)
    }
}

/**
 * @description 重写 window.open，拦截新标签页打开行为
 * @param {string} url 要打开的 URL
 * @param {string} target 目标窗口
 * @param {string} features 窗口特性
 * @returns {Window|null} 返回当前 window 对象以兼容某些库的检测
 */
window.open = function (url, target, features) {
    console.log('[window.open] 拦截到 window.open 调用', { url, target, features })

    // 验证 URL
    if (!url || typeof url !== 'string') {
        console.warn('[window.open] 无效的 URL:', url)
        return null
    }

    // 使用异步方式确保在 WebView 事件循环中执行
    setTimeout(() => {
        handleNavigation(url)
    }, 0)

    // 返回当前 window 对象，防止某些库检测到 null 后使用原生 window.open
    return window
}

// 监听 DOMContentLoaded 事件，在页面完全加载后再次确保 window.open 被重写
document.addEventListener('DOMContentLoaded', () => {
    console.log('[DOMContentLoaded] 再次确保 window.open 已被重写')
    if (window.open.toString().indexOf('handleNavigation') === -1) {
        console.warn('[DOMContentLoaded] window.open 可能被覆盖，重新设置')
        window.open = function (url, target, features) {
            console.log('[window.open] 重新拦截 window.open 调用', { url, target, features })
            setTimeout(() => {
                handleNavigation(url)
            }, 0)
            return window
        }
    }
})

document.addEventListener('click', hookClick, { capture: true })
